import "./App.css";
import { Component } from "react";
class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      display: {},
      input: "",
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    try {
      let value = await fetch("https://pokeapi.co/api/v2/pokemon");
      let data = await value.json();
      this.setState({
        display: data,
      });
    } catch (error) {
      console.log({ error: error.message });
    }
  };
  search = (event) => {
    this.setState({ input: event.target.value });
  };
  render() {
    const { input, display } = this.state;
    //console.log(display);
    let names = display.results ? display.results.map((el) => el.name) : [];
    return (
      <>
        <input type="text" value={input} onChange={this.search} />
        <div className="container">
          {names
            .filter((el) => el.toLowerCase().includes(input.toLowerCase()))
            .map((el) => (
              <div key={el}>{el}</div>
            ))}
        </div>
      </>
    );
  }
}

export default App;
