import "./App.css";
import { Component } from "react";
class App extends Component {
  constructor() {
    super();
    this.state = {
      result: "",
      input: "",
    };
  }
  componentDidMount() {
    this.fetchData();
  }
  fetchData = async () => {
    try {
      let value = await fetch("https://pokeapi.co/api/v2/pokemon");
      let data = await value.json();
      this.setState({
        result: data,
      });
    } catch (error) {
      console.log({ error: error.message });
    }
  };

  render() {
    const { input, result } = this.state;
    return (
      <>
        <input type="text" value={result.results[0].name} />
      </>
    );
  }
}

export default App;
